#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

const int MOD = 1000000007;

int main() {
    int n;
    std::cin >> n;

    std::string s, t;
    std::cin >> s >> t;

    std::vector<int> next(n + 1);
    std::vector<int> cnt(n + 1);
    std::vector<int> dp(n + 1);

    for (int i = 1; i <= n; ++i) {
        next[i] = i + 1;
        cnt[i] = 1;
    }

    for (int i = 1; i <= n; ++i) {
        if (s[i - 1] == t[i - 1]) {
            next[i] = next[i - 1];
            cnt[i] = (cnt[i - 1] + cnt[i]) % MOD;
        }
    }

    for (int i = n; i >= 1; --i) {
        if (s[i - 1] == t[i - 1]) {
            dp[i] = (dp[i + 1] + cnt[i]) % MOD;
        } else {
            dp[i] = dp[i + 1];
        }
    }

    std::cout << dp[1] << std::endl;

    return 0;
}
