#include <iostream>
#include <string>
#include <unordered_set>

int main() {
    std::string s;
    std::cin >> s;

    std::unordered_set<char> uniqueChars(s.begin(), s.end());

    if (s.length() == uniqueChars.size()) {
        std::cout << "yes" << std::endl;
    } else {
        std::cout << "no" << std::endl;
    }

    return 0;
}
