import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static int[][] dp;
    static String s, t;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int n = Integer.parseInt(br.readLine());
        s = br.readLine();
        t = br.readLine();
        dp = new int[n + 1][n + 1];
        for (int i = 0; i <= n; i++) {
            Arrays.fill(dp[i], -1);
        }
        pw.println(solve(1, n));
        pw.close();
    }

    static int solve(int i, int j) {
        if (i == j) return 1;
        if (dp[i][j] != -1) return dp[i][j];
        int ans = 0;
        for (int k = i + 1; k < j; k++) {
            if (s.charAt(i - 1) == s.charAt(k - 1) && t.charAt(i - 1) == t.charAt(k - 1)) {
                ans = (ans + solve(i, k) * solve(k, j)) % MOD;
            }
        }
        return dp[i][j] = ans;
    }
}
