#include <stdio.h>

int main() {
    int n, i, sum = 0;
    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        int s;
        scanf("%d", &s);
        sum += s;
    }
    if (sum % 10 != 0) {
        printf("%d\n", sum);
    } else {
        printf("0\n");
    }
    return 0;
}
