import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int sum = 0;
        for (int i = 0; i < n; i++) {
            int s = scanner.nextInt();
            sum += s;
        }
        for (int i = 0; i < n; i++) {
            int s = scanner.nextInt();
            sum -= s;
            if (sum % 10 != 0) {
                System.out.println(sum);
                break;
            }
            sum += s;
        }
        if (sum % 10 == 0) {
            System.out.println(0);
        }
    }
}
