#include <stdio.h>
#include <stdlib.h>

int main() {
    int n, a, b;
    scanf("%d %d %d", &n, &a, &b);

    int min = a;
    int max = a + (n - 1) * (b - a);

    int count = 0;
    for (int i = min; i <= max; i++) {
        count++;
    }

    printf("%d\n", count);

    return 0;
}
