#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define MAX_N 300000

typedef struct {
    int M;
    int C[MAX_N];
} Customer;

int compare(const void *a, const void *b) {
    Customer *c1 = (Customer *)a;
    Customer *c2 = (Customer *)b;
    return c2->C[0] - c1->C[0];
}

int main() {
    int N;
    scanf("%d", &N);

    Customer *customers = (Customer *)malloc(N * sizeof(Customer));
    for (int i = 0; i < N; i++) {
        scanf("%d", &customers[i].M);
        for (int j = 0; j < customers[i].M; j++) {
            scanf("%d", &customers[i].C[j]);
        }
    }

    qsort(customers, N, sizeof(Customer), compare);

    int max_sales = 0;
    int i = 0;
    while (i < N) {
        int j = i;
        while (j < N && customers[j].C[0] == customers[i].C[0]) {
            j++;
        }
        int k = i;
        while (k < j) {
            int max_index = 0;
            for (int l = 1; l < customers[k].M; l++) {
                if (customers[k].C[l] > customers[k].C[max_index]) {
                    max_index = l;
                }
            }
            max_sales = MAX(max_sales, customers[k].C[max_index]);
            for (int l = max_index; l < customers[k].M - 1; l++) {
                customers[k].C[l] = customers[k].C[l + 1];
            }
            customers[k].M--;
            k++;
        }
        i = j;
    }

    printf("%d\n", max_sales);
    free(customers);
    return 0;
}
