#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int main() {
    char s[MAXN];
    int n, i, j, k, l, sum = 0;
    int up[MAXN], down[MAXN];

    scanf("%s", s);
    n = strlen(s);

    for (i = 0; i < n; i++) {
        if (s[i] == 'U') {
            up[i] = 1;
            down[i] = 0;
        } else {
            up[i] = 0;
            down[i] = 1;
        }
    }

    for (i = 1; i < n; i++) {
        if (up[i]) {
            up[i] += up[i - 1];
        } else {
            up[i] = up[i - 1];
        }
    }

    for (i = n - 2; i >= 0; i--) {
        if (down[i]) {
            down[i] += down[i + 1];
        } else {
            down[i] = down[i + 1];
        }
    }

    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            k = abs(i - j);
            l = k;
            if (i < j) {
                l += up[j - 1] - up[i - 1];
            } else {
                l += down[j - 1] - down[i - 1];
            }
            sum += l;
        }
    }

    printf("%d\n", sum);

    return 0;
}
