import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String s = scanner.next();
        int n = s.length();
        int[] up = new int[n];
        int[] down = new int[n];
        int[] upCount = new int[n];
        int[] downCount = new int[n];

        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == 'U') {
                up[i] = 1;
                upCount[i] = 1;
            } else {
                down[i] = 1;
                downCount[i] = 1;
            }
        }

        for (int i = 1; i < n; i++) {
            if (s.charAt(i) == 'U') {
                up[i] += up[i - 1];
                upCount[i] += upCount[i - 1];
            } else {
                down[i] += down[i - 1];
                downCount[i] += downCount[i - 1];
            }
        }

        for (int i = n - 2; i >= 0; i--) {
            if (s.charAt(i) == 'D') {
                up[i] += up[i + 1];
                upCount[i] += upCount[i + 1];
            } else {
                down[i] += down[i + 1];
                downCount[i] += downCount[i + 1];
            }
        }

        long sum = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (s.charAt(i) == 'U' && s.charAt(j) == 'D') {
                    sum += upCount[j] + downCount[i] + Math.abs(i - j);
                } else if (s.charAt(i) == 'D' && s.charAt(j) == 'U') {
                    sum += downCount[j] + upCount[i] + Math.abs(i - j);
                }
            }
        }

        System.out.println(sum);
    }
}
