#include <stdio.h>
#include <stdlib.h>

#define MAX_N 2000
#define MAX_M 2000
#define MAX_Q 200000

int n, m, q;
int s[MAX_N + 5][MAX_M + 5];
int vis[MAX_N + 5][MAX_M + 5];
int cnt[MAX_N + 5][MAX_M + 5];
int ans[MAX_Q + 5];

void dfs(int x, int y) {
    if (x < 1 || x > n || y < 1 || y > m || vis[x][y] || !s[x][y]) return;
    vis[x][y] = 1;
    cnt[x][y] = 1;
    dfs(x + 1, y);
    dfs(x - 1, y);
    dfs(x, y + 1);
    dfs(x, y - 1);
}

void solve(int x1, int y1, int x2, int y2) {
    int i, j, k, l;
    for (i = 1; i <= n; i++) {
        for (j = 1; j <= m; j++) {
            vis[i][j] = 0;
            cnt[i][j] = 0;
        }
    }
    for (i = x1; i <= x2; i++) {
        for (j = y1; j <= y2; j++) {
            if (s[i][j]) dfs(i, j);
        }
    }
    int res = 0;
    for (i = x1; i <= x2; i++) {
        for (j = y1; j <= y2; j++) {
            res += cnt[i][j];
        }
    }
    ans[1]++;
    printf("%d\n", res);
}

int main() {
    scanf("%d %d %d", &n, &m, &q);
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            scanf("%d", &s[i][j]);
        }
    }
    for (int i = 1; i <= q; i++) {
        int x1, y1, x2, y2;
        scanf("%d %d %d %d", &x1, &y1, &x2, &y2);
        solve(x1, y1, x2, y2);
    }
    return 0;
}
