#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 2005, M = 2005;

int n, m, q;
int s[N][M];
int id[N][M];
int dfn[N][M];
int low[N][M];
int stk[N * M];
int top;
int cnt;
int ans[N];
int vis[N][M];

void dfs(int x, int y) {
    dfn[x][y] = low[x][y] = ++cnt;
    stk[++top] = cnt;
    vis[x][y] = 1;
    int dx[] = {-1, 0, 1, 0};
    int dy[] = {0, -1, 0, 1};
    for (int i = 0; i < 4; i++) {
        int nx = x + dx[i], ny = y + dy[i];
        if (nx >= 1 && nx <= n && ny >= 1 && ny <= m && s[nx][ny] && !dfn[nx][ny]) {
            dfs(nx, ny);
            low[x][y] = min(low[x][y], low[nx][ny]);
        } else if (nx >= 1 && nx <= n && ny >= 1 && ny <= m && s[nx][ny] && dfn[nx][ny]) {
            low[x][y] = min(low[x][y], dfn[nx][ny]);
        }
    }
    if (low[x][y] == dfn[x][y]) {
        int tmp = 0;
        do {
            id[x][y] = ++tmp;
            vis[x][y] = 0;
            y = stk[top--];
        } while (x != stk[top]);
        id[x][y] = tmp;
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin >> n >> m >> q;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            cin >> s[i][j];
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            if (s[i][j] && !dfn[i][j]) {
                dfs(i, j);
            }
        }
    }
    for (int i = 1; i <= q; i++) {
        int x1, y1, x2, y2;
        cin >> x1 >> y1 >> x2 >> y2;
        int res = 0;
        for (int j = x1; j <= x2; j++) {
            for (int k = y1; k <= y2; k++) {
                if (s[j][k]) {
                    res += id[j][k];
                }
            }
        }
        cout << res << endl;
    }
    return 0;
}
