from collections import deque

def dfs(i, j, grid, visited):
    if 0 <= i < len(grid) and 0 <= j < len(grid[0]) and grid[i][j] == 1 and not visited[i][j]:
        visited[i][j] = True
        queue = deque([(i, j)])
        while queue:
            x, y = queue.popleft()
            for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nx, ny = x + dx, y + dy
                if 0 <= nx < len(grid) and 0 <= ny < len(grid[0]) and grid[nx][ny] == 1 and not visited[nx][ny]:
                    visited[nx][ny] = True
                    queue.append((nx, ny))

def count_components(grid, queries):
    n, m = len(grid), len(grid[0])
    visited = [[False] * m for _ in range(n)]
    components = 0
    for i in range(n):
        for j in range(m):
            if grid[i][j] == 1 and not visited[i][j]:
                dfs(i, j, grid, visited)
                components += 1
    for query in queries:
        x1, y1, x2, y2 = query
        subgrid = [row[y1-1:y2] for row in grid[x1-1:x2]]
        subgrid_visited = [[False] * len(subgrid[0]) for _ in range(len(subgrid))]
        subgrid_components = 0
        for i in range(len(subgrid)):
            for j in range(len(subgrid[0])):
                if subgrid[i][j] == 1 and not subgrid_visited[i][j]:
                    dfs(i, j, subgrid, subgrid_visited)
                    subgrid_components += 1
        print(subgrid_components)

