#include <stdio.h>
#include <string.h>

#define MAX 60

int main() {
    long long a, b, ans = 0;
    int i, j, k, cnt = 0;
    char str[65];

    scanf("%lld%lld", &a, &b);

    for (i = MAX - 1; i >= 0; i--) {
        str[i] = '0';
    }

    for (i = MAX - 1; i >= 0; i--) {
        if ((a >> i) & 1) {
            str[i] = '1';
        }
    }

    for (i = MAX - 1; i >= 0; i--) {
        if ((b >> i) & 1) {
            if (str[i] == '0') {
                cnt = 0;
                for (j = i - 1; j >= 0; j--) {
                    if (str[j] == '1') {
                        cnt++;
                    }
                }
                ans += (1LL << cnt);
            }
        }
    }

    printf("%lld\n", ans);

    return 0;
}
