#include <iostream>
#include <queue>
#include <vector>
#include <algorithm>

using namespace std;

struct Node {
    int fare;
    int index;
};

bool compareNodes(const Node& a, const Node& b) {
    return a.fare > b.fare;
}

int main() {
    int N;
    cin >> N;

    priority_queue<Node> pq;
    vector<vector<int>> customers(N);

    for (int i = 0; i < N; i++) {
        int M;
        cin >> M;
        customers[i].resize(M);
        for (int j = 0; j < M; j++) {
            cin >> customers[i][j];
        }
        if (!customers[i].empty()) {
            pq.push({customers[i][0], i});
        }
    }

    int maxFare = 0;
    while (!pq.empty()) {
        Node node = pq.top();
        pq.pop();
        if (customers[node.index].empty()) {
            continue;
        }
        maxFare = max(maxFare, customers[node.index][0]);
        customers[node.index].erase(customers[node.index].begin());
        if (!customers[node.index].empty()) {
            pq.push({customers[node.index][0], node.index});
        }
    }

    cout << maxFare << endl;

    return 0;
}
