#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

typedef struct {
    int x, v;
} Node;

int cmp(const void *a, const void *b) {
    Node *x = (Node *)a;
    Node *y = (Node *)b;
    return x->v - y->v;
}

int main() {
    int n;
    scanf("%d", &n);
    Node *nodes = (Node *)malloc(n * sizeof(Node));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &nodes[i].x, &nodes[i].v);
    }
    qsort(nodes, n, sizeof(Node), cmp);
    int ans = 1;
    for (int i = 0; i < n; i++) {
        int j = i;
        while (j < n && nodes[j].x < nodes[i].x + nodes[i].v * (j - i)) {
            j++;
        }
        ans = (ans * (j - i)) % MOD;
        i = j - 1;
    }
    printf("%d\n", ans);
    free(nodes);
    return 0;
}
