#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MOD 1000000007
#define MAXN 300005

typedef long long ll;

ll gcd(ll a, ll b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

ll qpow(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int q;
    scanf("%d", &q);
    ll x[MAXN], y[MAXN];
    for (int i = 1; i <= q; i++) {
        scanf("%lld%lld", &x[i], &y[i]);
    }
    for (int i = 1; i <= q; i++) {
        ll g = gcd(x[i], y[i]);
        ll ans = 0, cnt = 0;
        for (ll j = 1; j * j <= g; j++) {
            if (g % j == 0) {
                ll t = j;
                while (t <= x[i] && t <= y[i]) {
                    ans = ans > t ? ans : t;
                    cnt = (cnt + (x[i] / t - 1) * (y[i] / t - 1)) % MOD;
                    t = t * j / gcd(t, j);
                }
                t = g / j;
                while (t <= x[i] && t <= y[i]) {
                    ans = ans > t ? ans : t;
                    cnt = (cnt + (x[i] / t - 1) * (y[i] / t - 1)) % MOD;
                    t = t * j / gcd(t, j);
                }
            }
        }
        printf("%lld %lld\n", ans, (cnt + MOD - qpow(ans, 2) * qpow(x[i] / ans, 2) % MOD * qpow(y[i] / ans, 2) % MOD) % MOD);
    }
    return 0;
}
