#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

long long gcd(long long a, long long b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

long long countPairs(long long x, long long y) {
    long long g = gcd(x, y);
    long long ans = 0;
    for (long long i = 1; i * i <= g; i++) {
        if (g % i == 0) {
            long long j = g / i;
            ans += max(0LL, min(x / i, y / j) - 1);
            ans += max(0LL, min(x / j, y / i) - 1);
        }
    }
    return ans;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int q;
    cin >> q;
    vector<pair<long long, long long>> queries(q);
    for (int i = 0; i < q; i++) {
        cin >> queries[i].first >> queries[i].second;
    }

    for (int i = 0; i < q; i++) {
        long long x = queries[i].first;
        long long y = queries[i].second;
        long long g = gcd(x, y);
        long long ans = 0;
        for (long long i = 1; i * i <= g; i++) {
            if (g % i == 0) {
                long long j = g / i;
                ans = max(ans, min(x / i, y / j));
                ans = max(ans, min(x / j, y / i));
            }
        }
        cout << ans * 2 - 1 << ' ' << countPairs(x, y) << '\n';
    }

    return 0;
}
