import java.io.*;
import java.util.*;

public class Main {
    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    static class Pair implements Comparable<Pair> {
        int first;
        int second;

        public Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }

        @Override
        public int compareTo(Pair o) {
            return Integer.compare(o.second, this.second);
        }
    }

    public static void main(String[] args) {
        FastReader sc = new FastReader();
        int n = sc.nextInt();
        Pair[] a = new Pair[n + 1];
        for (int i = 1; i <= n; i++) {
            int m = sc.nextInt();
            int[] c = new int[m + 1];
            for (int j = 1; j <= m; j++) {
                c[j] = sc.nextInt();
            }
            Arrays.sort(c, 1, m + 1);
            a[i] = new Pair(i, c[1]);
        }
        Arrays.sort(a, 1, n + 1);
        long ans = 0;
        int i = 1;
        while (i <= n) {
            int j = i;
            while (j <= n && a[j].first == a[i].first) {
                ans = Math.max(ans, a[j].second);
                j++;
            }
            i = j;
        }
        System.out.println(ans);
    }
}
