#include <stdio.h>
#include <stdlib.h>

int main() {
    int H, W;
    char c;

    // Read height and width
    scanf("%d %d", &H, &W);

    // Print top border
    printf("#");
    for (int i = 0; i < W; i++) {
        printf("#");
    }
    printf("#\n");

    // Read and print each row
    for (int i = 0; i < H; i++) {
        // Print left border
        printf("#");
        // Read and print each character
        for (int j = 0; j < W; j++) {
            scanf(" %c", &c);
            printf("%c", c);
        }
        // Print right border
        printf("#\n");
    }

    // Print bottom border
    printf("#");
    for (int i = 0; i < W; i++) {
        printf("#");
    }
    printf("#\n");

    return 0;
}
