#include <stdio.h>
#include <stdlib.h>

#define N 3000
#define MAX 10000

int cmp(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int check(int x1, int y1, int x2, int y2, int x3, int y3) {
    if ((x2 - x1) * (y3 - y1) == (x3 - x1) * (y2 - y1))
        return 1;
    return 0;
}

int main() {
    int n, k, i, j, count = 0;
    int x[N], y[N];

    scanf("%d %d", &n, &k);
    for (i = 0; i < n; i++)
        scanf("%d %d", &x[i], &y[i]);

    qsort(x, n, sizeof(int), cmp);
    qsort(y, n, sizeof(int), cmp);

    for (i = 0; i < n; i++) {
        for (j = i + 1; j < n; j++) {
            if (x[i] == x[j] && y[i] == y[j])
                continue;
            count = 0;
            for (int p = 0; p < n; p++) {
                if (x[p] == x[i] && y[p] == y[i])
                    count++;
                if (x[p] == x[j] && y[p] == y[j])
                    count++;
            }
            if (count >= k)
                break;
        }
        if (j < n)
            break;
    }

    if (j < n)
        printf("1\n");
    else
        printf("0\n");

    return 0;
}
