#include <iostream>
#include <vector>
#include <algorithm>
#include <cstring>

using namespace std;

const int N = 305;
const int M = 305;
const int MOD = 1e9 + 7;

int n, m;
int l[M], r[M], x[M];
int dp[N][N][4];
int pre[N][N][4];

void add(int &x, int y) {
    x += y;
    if (x >= MOD) x -= MOD;
}

void sub(int &x, int y) {
    x -= y;
    if (x < 0) x += MOD;
}

int main() {
    cin >> n >> m;
    for (int i = 1; i <= m; i++) {
        cin >> l[i] >> r[i] >> x[i];
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 0; j <= 3; j++) {
            dp[i][i][j] = 1;
        }
    }

    for (int len = 2; len <= n; len++) {
        for (int i = 1; i + len - 1 <= n; i++) {
            int j = i + len - 1;
            for (int k = 0; k <= 3; k++) {
                dp[i][j][k] = 0;
                for (int c = 0; c <= k; c++) {
                    add(dp[i][j][k], dp[i][j - 1][c] * pre[j - 1][j - 1][k - c]);
                }
            }
            for (int i = 1; i <= j; i++) {
                for (int k = 0; k <= 3; k++) {
                    pre[i][j][k] = 0;
                    for (int c = 0; c <= k; c++) {
                        add(pre[i][j][k], pre[i - 1][j][c] * dp[i][j - 1][c]);
                    }
                }
            }
        }
    }

    int ans = 0;
    for (int i = 1; i <= m; i++) {
        int l = l[i], r = r[i], x = x[i];
        for (int j = 0; j <= 3; j++) {
            sub(dp[l][r][j], dp[l][r][x]);
        }
    }

    for (int i = 0; i <= 3; i++) {
        add(ans, dp[1][n][i]);
    }

    cout << ans << endl;

    return 0;
}
