import java.io.*;
import java.util.*;

public class Main {
    static final int N = 310;
    static final int M = 310;
    static final int MOD = (int) 1e9 + 7;
    static int n, m;
    static int[][] l = new int[M][2];
    static int[] x = new int[M];
    static int[][] f = new int[N][N];
    static int[][] g = new int[N][N];
    static int[][] h = new int[N][N];
    static int[][] dp = new int[N][N];
    static int[][] pre = new int[N][N];
    static int[][] suf = new int[N][N];

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        n = sc.nextInt();
        m = sc.nextInt();
        for (int i = 1; i <= m; i++) {
            l[i][0] = sc.nextInt();
            l[i][1] = sc.nextInt();
            x[i] = sc.nextInt();
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                f[i][j] = g[i][j] = h[i][j] = 0;
            }
        }
        for (int i = 1; i <= m; i++) {
            for (int j = l[i][0]; j <= l[i][1]; j++) {
                f[j][l[i][1]] |= (1 << (x[i] - 1));
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                g[i][j] = (g[i][j - 1] + f[i][j]) % MOD;
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                h[i][j] = (h[i - 1][j] + g[i][j]) % MOD;
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                pre[i][j] = (pre[i - 1][j] + h[i][j]) % MOD;
            }
        }
        for (int i = n; i >= 1; i--) {
            for (int j = 1; j <= n; j++) {
                suf[i][j] = (suf[i + 1][j] + h[i][j]) % MOD;
            }
        }
        for (int i = 1; i <= n; i++) {
            dp[i][i] = 1;
        }
        for (int len = 1; len <= n; len++) {
            for (int l = 1; l + len - 1 <= n; l++) {
                int r = l + len - 1;
                for (int i = 0; i <= 7; i++) {
                    int val = 0;
                    for (int j = 0; j < 3; j++) {
                        if (((i >> j) & 1) == 1) {
                            val = (val + pre[l][j + 1] + suf[r + 1][j + 1]) % MOD;
                        }
                    }
                    dp[l][r] = (dp[l][r] + val * (g[r][r] - g[l - 1][r])) % MOD;
                }
            }
        }
        pw.println(dp[1][n]);
        pw.close();
    }

    static class Scanner {
        BufferedReader br;
        StringTokenizer st;

        public Scanner(InputStream in) {
            br = new BufferedReader(new InputStreamReader(in));
        }

        public String next() throws IOException {
            while (st == null || !st.hasMoreTokens()) {
                st = new StringTokenizer(br.readLine());
            }
            return st.nextToken();
        }

        public int nextInt() throws IOException {
            return Integer.parseInt(next());
        }
    }
}
