MOD = 10**9 + 7

def add(a, b):
    return (a + b) % MOD

def mul(a, b):
    return (a * b) % MOD

def pow(a, b):
    res = 1
    while b:
        if b & 1:
            res = mul(res, a)
        a = mul(a, a)
        b >>= 1
    return res

def solve():
    N, M = map(int, input().split())
    l = [0] * (N + 1)
    r = [0] * (N + 1)
    x = [0] * (M + 1)
    for i in range(1, M + 1):
        l[i], r[i], x[i] = map(int, input().split())
    dp = [[0] * (N + 1) for _ in range(4)]
    dp[0][0] = 1
    for i in range(1, M + 1):
        for j in range(4):
            for k in range(l[i], r[i] + 1):
                if dp[j][k - 1]:
                    if x[i] == 1:
                        dp[j][k] = add(dp[j][k], dp[j][k - 1])
                    elif x[i] == 2:
                        dp[j + 1][k] = add(dp[j + 1][k], dp[j][k - 1])
                    elif x[i] == 3:
                        for t in range(j + 1, 4):
                            dp[t][k] = add(dp[t][k], dp[j][k - 1])
    ans = 0
    for i in range(4):
        ans = add(ans, dp[i][N])
    print(ans)

if __name__ == "__main__":
    solve()
