#include <stdio.h>
#include <stdlib.h>

#define MAX_H 105
#define MAX_W 105

int h, w;
char grid[MAX_H][MAX_W];
int visited[MAX_H][MAX_W];
int leaf_count;

void dfs(int i, int j) {
    if (i < 0 || i >= h || j < 0 || j >= w || visited[i][j] || grid[i][j] == 'S' || grid[i][j] == 'T') {
        return;
    }

    visited[i][j] = 1;
    leaf_count++;

    dfs(i - 1, j);
    dfs(i + 1, j);
    dfs(i, j - 1);
    dfs(i, j + 1);
}

int main() {
    scanf("%d %d", &h, &w);
    for (int i = 0; i < h; i++) {
        scanf("%s", grid[i]);
    }

    int si, sj;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (grid[i][j] == 'S') {
                si = i;
                sj = j;
            }
        }
    }

    int ans = -1;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (grid[i][j] == 'o') {
                leaf_count = 0;
                dfs(i, j);
                if (leaf_count == 0) {
                    ans = 0;
                    break;
                }
                if (si == i || sj == j) {
                    ans = leaf_count;
                }
            }
        }
        if (ans != -1) {
            break;
        }
    }

    printf("%d\n", ans);
    return 0;
}
