#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, k;
    cin >> n >> k;

    vector<pair<int, int>> points(n);
    for (int i = 0; i < n; i++) {
        cin >> points[i].first >> points[i].second;
    }

    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            int x1 = points[i].first, y1 = points[i].second;
            int x2 = points[j].first, y2 = points[j].second;

            int x3, y3;
            for (int p = 0; p < n; p++) {
                if (p != i && p != j) {
                    x3 = points[p].first, y3 = points[p].second;
                    if ((x2 - x1) * (y3 - y1) != (y2 - y1) * (x3 - x1)) {
                        goto next;
                    }
                }
            }

            cout << 1 << endl;
            return 0;
        next:;
        }
    }

    cout << 0 << endl;
    return 0;
}
