#include <iostream>
#include <vector>
#include <queue>
#include <set>
#include <string>

using namespace std;

const int dx[] = {0, 0, 1, -1};
const int dy[] = {1, -1, 0, 0};

int main() {
    int h, w;
    cin >> h >> w;

    vector<string> a(h);
    for (int i = 0; i < h; i++) {
        cin >> a[i];
    }

    int sx, sy;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (a[i][j] == 'S') {
                sx = i;
                sy = j;
            }
        }
    }

    vector<vector<int>> dist(h, vector<int>(w, -1));
    queue<pair<int, int>> q;
    q.push({sx, sy});
    dist[sx][sy] = 0;

    set<pair<int, int>> visited;
    visited.insert({sx, sy});

    int ans = 0;
    while (!q.empty()) {
        int x = q.front().first;
        int y = q.front().second;
        q.pop();

        for (int k = 0; k < 4; k++) {
            int nx = x + dx[k];
            int ny = y + dy[k];

            if (nx < 0 || nx >= h || ny < 0 || ny >= w || a[nx][ny] == 'T' || visited.count({nx, ny})) {
                continue;
            }

            if (a[nx][ny] == 'o') {
                ans++;
            }

            visited.insert({nx, ny});
            dist[nx][ny] = dist[x][y] + 1;
            q.push({nx, ny});
        }
    }

    if (dist[sx][sy] == -1) {
        cout << -1 << endl;
    } else {
        cout << ans << endl;
    }

    return 0;
}
