                            }
                        }
                    }
                }
            }
        }
        for (int i = 0; i < H; i++) {
            for (int j = 0; j < W; j++) {
                if (grid[i][j] == 'o') {
                    int cnt = 0;
                    for (int k = 0; k < 4; k++) {
                        int nr = i + dr[k];
                        int nc = j + dc[k];
                        if (0 <= nr && nr < H && 0 <= nc && nc < W && col[nr][nc] == 1) {
                            cnt++;
                        }
                    }
                    if (cnt == 1) {
                        ans++;
                        for (int k = 0; k < 4; k++) {
                            int nr = i + dr[k];
                            int nc = j + dc[k];
                            if (0 <= nr && nr < H && 0 <= nc && nc < W && col[nr][nc] == 1) {
                                row[nr][nc] = 0;
                                col[nr][nc] = 0;
                            }
                        }
                    }
                }
            }
        }
        if (row[H - 1][W - 1] == 1) {
            System.out.println(-1);
        } else {
            System.out.println(ans);
        }
    }
}
