#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define N 1000
#define M 2000

typedef struct {
    int to;
    int cost;
} Edge;

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    Edge edges[m];
    for (int i = 0; i < m; i++) {
        int a, b, c;
        scanf("%d %d %d", &a, &b, &c);
        edges[i].to = b;
        edges[i].cost = c;
    }

    int dp[n + 1];
    for (int i = 0; i <= n; i++) {
        dp[i] = INT_MIN;
    }
    dp[1] = 0;

    for (int i = 1; i < n; i++) {
        for (int j = 0; j < m; j++) {
            if (edges[j].to == i) {
                dp[i + 1] = dp[i + 1] > dp[i] + edges[j].cost ? dp[i + 1] : dp[i] + edges[j].cost;
            }
        }
    }

    if (dp[n] == INT_MIN) {
        printf("inf\n");
    } else {
        printf("%d\n", dp[n]);
    }

    return 0;
}
