#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

struct Edge {
    int to;
    long long cost;
};

int main() {
    int N, M;
    cin >> N >> M;

    vector<vector<Edge>> graph(N + 1);
    for (int i = 0; i < M; i++) {
        int a, b;
        long long c;
        cin >> a >> b >> c;
        graph[a].push_back({b, c});
    }

    vector<long long> dp(N + 1, numeric_limits<long long>::min());
    dp[1] = 0;

    priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<pair<long long, int>>> pq;
    pq.push({0, 1});

    while (!pq.empty()) {
        long long cost = pq.top().first;
        int node = pq.top().second;
        pq.pop();

        for (const auto& edge : graph[node]) {
            int to = edge.to;
            long long newCost = cost + edge.cost;

            if (dp[to] < newCost) {
                dp[to] = newCost;
                pq.push({newCost, to});
            }
        }
    }

    if (dp[N] == numeric_limits<long long>::min()) {
        cout << "inf" << endl;
    } else {
        cout << dp[N] << endl;
    }

    return 0;
}
