import itertools
import math

def is_collinear(points):
    for r in range(2, len(points) + 1):
        for combination in itertools.combinations(points, r):
            x_coords = [point[0] for point in combination]
            y_coords = [point[1] for point in combination]
            if len(set(x_coords)) == 1 or len(set(y_coords)) == 1:
                return True
            slope = (y_coords[1] - y_coords[0]) * (x_coords[0] - x_coords[0]) 
            for i in range(1, len(combination)):
                if (y_coords[i] - y_coords[0]) * (x_coords[0] - x_coords[i]) != slope:
                    return False
            return True
    return False

n, k = map(int, input().split())
points = []
for _ in range(n):
    x, y = map(int, input().split())
    points.append((x, y))

if is_collinear(points[:k]):
    print(1)
else:
    print(0)
