#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 100005
#define M 100005

int n, m;
int a[M], b[M];
int fa[N], sz[N];
int vis[N];
int ans;

int find(int x) {
    if (fa[x] == x) return x;
    return fa[x] = find(fa[x]);
}

void merge(int x, int y) {
    x = find(x);
    y = find(y);
    if (x == y) return;
    if (sz[x] < sz[y]) {
        int temp = x;
        x = y;
        y = temp;
    }
    fa[y] = x;
    sz[x] += sz[y];
    vis[x] += vis[y];
}

int main() {
    scanf("%d%d", &n, &m);
    for (int i = 1; i <= n; i++) {
        fa[i] = i;
        sz[i] = 1;
    }
    for (int i = 1; i <= m; i++) {
        scanf("%d%d", &a[i], &b[i]);
        merge(a[i], b[i]);
    }
    for (int i = 1; i <= n; i++) {
        if (vis[i] & 1) {
            ans = 1;
            break;
        }
    }
    if (ans) printf("NO\n");
    else printf("YES\n");
    return 0;
}
