import java.io.*;
import java.util.*;

public class Main {
    static int N, M;
    static int[] par;
    static int[] depth;
    static int[] cnt;
    static int[] val;
    static List<Integer>[] g;
    static List<int[]>[] queries;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        N = Integer.parseInt(st.nextToken());
        M = Integer.parseInt(st.nextToken());

        g = new List[N + 1];
        for (int i = 0; i <= N; i++) {
            g[i] = new ArrayList<>();
        }
        queries = new List[N + 1];
        for (int i = 0; i <= N; i++) {
            queries[i] = new ArrayList<>();
        }

        for (int i = 0; i < M; i++) {
            st = new StringTokenizer(br.readLine());
            int a = Integer.parseInt(st.nextToken());
            int b = Integer.parseInt(st.nextToken());
            g[a].add(b);
            g[b].add(a);
            queries[a].add(new int[]{b, i});
            queries[b].add(new int[]{a, i});
        }

        par = new int[N + 1];
        depth = new int[N + 1];
        cnt = new int[N + 1];
        val = new int[N + 1];

        dfs(1, 0);

        for (int i = 1; i <= N; i++) {
            for (int[] query : queries[i]) {
                int j = query[0];
                int idx = query[1];
                if (depth[i] > depth[j]) {
                    int tmp = i;
                    i = j;
                    j = tmp;
                }
                cnt[j] += cnt[i] + (val[i] % 2 == 1 ? 1 : 0);
                val[j]++;
            }
        }

        boolean ok = true;
        for (int i = 1; i <= N; i++) {
            for (int j : g[i]) {
                if (depth[i] > depth[j]) {
                    int tmp = i;
                    i = j;
                    j = tmp;
                }
                if (cnt[i] % 2 == 1) {
                    ok = false;
                    break;
                }
                val[i] += val[j];
            }
        }

        pw.println(ok ? "YES" : "NO");
        pw.close();
    }

    static void dfs(int cur, int par) {
        par[cur] = par;
        depth[cur] = depth[par] + 1;
        for (int next : g[cur]) {
            if (next == par) continue;
            dfs(next, cur);
        }
    }
}
