from collections import defaultdict
import sys

input = sys.stdin.readline

N = int(input().split()[0])
M = int(input().split()[0])

adj = defaultdict(list)
for _ in range(M):
    u, v = map(int, input().split())
    adj[u].append(v)
    adj[v].append(u)

par = [i for i in range(N + 1)]
sz = [1] * (N + 1)

def find(x):
    if par[x] != x:
        par[x] = find(par[x])
    return par[x]

def union(x, y):
    x = find(x)
    y = find(y)
    if x != y:
        if sz[x] < sz[y]:
            x, y = y, x
        par[y] = x
        sz[x] += sz[y]

for i in range(1, N + 1):
    for j in adj[i]:
        if j < i:
            union(i, j)

odd = set()
for i in range(1, N + 1):
    if find(i) == i:
        for j in adj[i]:
            if find(j) == i:
                if (j, i) in odd or (i, j) in odd:
                    print("NO")
                    exit()
                odd.add((i, j))

print("YES")
