#include <iostream>
#include <vector>
#include <queue>
#include <cstring>

using namespace std;

const int MAX_H = 800;
const int MAX_W = 800;
const int MAX_K = 800 * 800;

int H, W, K;
char grid[MAX_H + 1][MAX_W + 1];
int dist[MAX_H + 1][MAX_W + 1];
int queueSize;
int dx[] = {-1, 0, 1, 0};
int dy[] = {0, -1, 0, 1};

struct Node {
    int x, y, d;
};

void bfs(int sx, int sy) {
    queue<Node> q;
    dist[sx][sy] = 0;
    q.push({sx, sy, 0});
    while (queueSize--) {
        Node node = q.front();
        q.pop();
        int x = node.x, y = node.y, d = node.d;
        for (int i = 0; i < 4; i++) {
            int nx = x + dx[i], ny = y + dy[i];
            if (nx < 1 || nx > H || ny < 1 || ny > W || grid[nx][ny] == '#' || dist[nx][ny] <= d) continue;
            dist[nx][ny] = d + 1;
            q.push({nx, ny, d + 1});
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> H >> W >> K;
    for (int i = 1; i <= H; i++) {
        for (int j = 1; j <= W; j++) {
            cin >> grid[i][j];
        }
    }

    int sx, sy;
    for (int i = 2; i < H; i++) {
        for (int j = 2; j < W; j++) {
            if (grid[i][j] == 'S') {
                sx = i, sy = j;
                break;
            }
        }
    }

    memset(dist, 0x3f, sizeof dist);
    queueSize = 1;
    bfs(sx, sy);

    int ans = 0;
    for (int i = 1; i <= H; i++) {
        for (int j = 1; j <= W; j++) {
            if (grid[i][j] == '#') {
                int d = dist[i][j];
                if (d <= K) {
                    ans = max(ans, d);
                }
            }
        }
    }

    cout << ans << '\n';

    return 0;
}
