#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>
#include <limits.h>
#include <time.h>
#include <assert.h>
#include <ctype.h>
#include <stdbool.h>
#include <stddef.h>
#include <stdint.h>
#include <inttypes.h>

#define MAXN 100005
#define INF 0x3f3f3f3f

int n;
int p[MAXN];
int s[MAXN], t[MAXN];
int e[MAXN][2];
int d[MAXN];
int f[MAXN][2];

void add(int u, int v) {
    e[u][0] = v;
    e[u][1]++;
}

void dfs(int u, int fa) {
    d[u] = d[fa] + 1;
    f[u][0] = p[u];
    f[u][1] = 0;
    for (int i = 0; i < e[u][1]; i++) {
        int v = e[u][i];
        if (v == fa) continue;
        dfs(v, u);
        f[u][0] = max(f[u][0], f[v][0] + p[u]);
        f[u][1] = max(f[u][1], f[v][1] + p[u]);
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &p[i]);
    }
    for (int i = 1; i < n; i++) {
        scanf("%d%d", &s[i], &t[i]);
        add(s[i], t[i]);
        add(t[i], s[i]);
    }
    dfs(1, 0);
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans = max(ans, f[i][0]);
        ans = max(ans, f[i][1]);
    }
    printf("%d\n", ans - (n - 1));
    return 0;
}
