from collections import deque
import sys

def solve():
    h, w, k = map(int, input().split())
    grid = [list(input()) for _ in range(h)]
    for i in range(h):
        for j in range(w):
            if grid[i][j] == 'S':
                sx, sy = i, j
                grid[i][j] = '.'
    dist = [[float('inf')] * w for _ in range(h)]
    dist[sx][sy] = 0
    q = deque([(sx, sy, 0)])
    while q:
        x, y, d = q.popleft()
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < h and 0 <= ny < w and grid[nx][ny] != '#':
                if dist[nx][ny] > d + 1:
                    dist[nx][ny] = d + 1
                    q.append((nx, ny, d + 1))
            else:
                for i in range(max(0, nx - k), min(h, nx + k + 1)):
                    for j in range(max(0, ny - k), min(w, ny + k + 1)):
                        if grid[i][j] == '#':
                            grid[i][j] = '.'
                            if dist[i][j] > d + 1:
                                dist[i][j] = d + 1
                                q.append((i, j, d + 1))
    print(dist[sx][sy] - 1 if dist[sx][sy] != float('inf') else -1)

if __name__ == '__main__':
    solve()
