#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int n, cnt, ans;
int head[MAXN], to[MAXN << 1], nxt[MAXN << 1], vis[MAXN], col[MAXN];
int a[MAXN], b[MAXN];

void add(int x, int y) {
    to[cnt] = y;
    nxt[cnt] = head[x];
    head[x] = cnt++;
}

void dfs(int u, int fa) {
    for (int i = head[u]; i != -1; i = nxt[i]) {
        int v = to[i];
        if (v == fa) continue;
        dfs(v, u);
        col[v] = 1 - col[u];
    }
    if (col[u] == 1) {
        for (int i = head[u]; i != -1; i = nxt[i]) {
            int v = to[i];
            if (v != fa && col[v] == 0) {
                ans = 1;
                return;
            }
        }
    }
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i < n; i++) {
        scanf("%d %d", &a[i], &b[i]);
        add(a[i], b[i]);
        add(b[i], a[i]);
    }
    for (int i = 1; i <= n; i++) {
        if (!vis[i]) {
            dfs(i, 0);
            if (ans) break;
        }
    }
    if (ans) printf("First\n");
    else printf("Second\n");
    return 0;
}
