#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAXN = 1e5 + 5;

int n;
vector<int> g[MAXN];
int color[MAXN];
int depth[MAXN];

void dfs(int u, int p, int d) {
    depth[u] = d;
    for (int v : g[u]) {
        if (v == p) continue;
        dfs(v, u, d + 1);
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n;
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        g[u].push_back(v);
        g[v].push_back(u);
    }

    dfs(1, 0, 0);

    queue<int> q;
    for (int i = 1; i <= n; i++) {
        if (depth[i] % 2 == 0) {
            q.push(i);
            color[i] = 1;
        }
    }

    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : g[u]) {
            if (color[v] == 0) {
                color[v] = 3 - color[u];
                q.push(v);
            }
        }
    }

    for (int i = 1; i <= n; i++) {
        if (color[i] == 1) {
            cout << "First" << endl;
            return 0;
        }
    }

    cout << "Second" << endl;

    return 0;
}
