import java.io.*;
import java.util.*;

public class Main {
    static int N;
    static List<Integer>[] adj;
    static int[] color;
    static int[] depth;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st;

        N = Integer.parseInt(br.readLine());
        adj = new List[N + 1];
        for (int i = 1; i <= N; i++) {
            adj[i] = new ArrayList<>();
        }
        for (int i = 1; i < N; i++) {
            st = new StringTokenizer(br.readLine());
            int u = Integer.parseInt(st.nextToken());
            int v = Integer.parseInt(st.nextToken());
            adj[u].add(v);
            adj[v].add(u);
        }

        color = new int[N + 1];
        depth = new int[N + 1];
        dfs(1, 0);

        int white = 0;
        for (int i = 1; i <= N; i++) {
            if (color[i] == 0) white++;
        }

        if (white % 2 == 1) pw.println("First");
        else pw.println("Second");

        pw.close();
    }

    static void dfs(int u, int p) {
        for (int v : adj[u]) {
            if (v == p) continue;
            depth[v] = depth[u] + 1;
            dfs(v, u);
        }
    }
}
