#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005
#define MAXM 200005

typedef struct {
    int to, next;
} edge;

edge e[MAXM];
int head[MAXN], cnt = 1;

void add_edge(int u, int v) {
    e[++cnt].to = v;
    e[cnt].next = head[u];
    head[u] = cnt;
}

int dfn[MAXN], low[MAXN], vis[MAXN], idx = 0, cut[MAXN];
int n, m, s, t, ans = 1;

void tarjan(int u, int fa) {
    dfn[u] = low[u] = ++idx;
    vis[u] = 1;
    int cnt = 0;
    for (int i = head[u]; i; i = e[i].next) {
        int v = e[i].to;
        if (v == fa) continue;
        if (dfn[v]) low[u] = min(low[u], dfn[v]);
        else {
            tarjan(v, u);
            low[u] = min(low[u], low[v]);
            if (low[v] >= dfn[u]) cut[u] = 1;
            cnt++;
        }
    }
    if (cnt >= 2) cut[u] = 1;
}

int find(int u, int fa) {
    if (u == s) return 1;
    for (int i = head[u]; i; i = e[i].next) {
        int v = e[i].to;
        if (v == fa) continue;
        if (find(v, u)) {
            ans = 0;
            return 1;
        }
    }
    return 0;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i < n; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        add_edge(u, v);
        add_edge(v, u);
    }
    scanf("%d %d", &s, &t);
    tarjan(s, 0);
    if (cut[s]) {
        printf("NO\n");
        return 0;
    }
    find(t, 0);
    if (ans) printf("YES\n");
    else printf("NO\n");
    return 0;
}
