#include <iostream>
#include <vector>
#include <queue>
#include <set>

using namespace std;

const int MAXN = 1e5 + 5;

vector<int> g[MAXN];
vector<int> rg[MAXN];
int n;
int a[MAXN], b[MAXN], c[MAXN], d[MAXN];

void dfs(int u, int p) {
    for (int v : g[u]) {
        if (v == p) continue;
        dfs(v, u);
        rg[u].push_back(v);
    }
}

void solve() {
    cin >> n;
    for (int i = 1; i < n; i++) {
        cin >> a[i] >> b[i];
        g[a[i]].push_back(b[i]);
        g[b[i]].push_back(a[i]);
    }
    for (int i = 1; i < n; i++) {
        cin >> c[i] >> d[i];
    }
    dfs(1, 0);
    for (int i = 1; i < n; i++) {
        int u = c[i], v = d[i];
        if (u == v) {
            cout << "NO" << endl;
            return;
        }
        if (rg[u].size() != rg[v].size()) {
            cout << "NO" << endl;
            return;
        }
        set<int> s;
        for (int x : rg[u]) s.insert(x);
        for (int x : rg[v]) s.insert(x);
        if (s.size() != rg[u].size()) {
            cout << "NO" << endl;
            return;
        }
        bool ok = false;
        for (int x : rg[u]) {
            if (x == v) continue;
            if (s.find(x) != s.end()) {
                ok = true;
                break;
            }
        }
        if (!ok) {
            cout << "NO" << endl;
            return;
        }
    }
    cout << "YES" << endl;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    int t = 1;
    while (t--) solve();
    return 0;
}
