#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int* p = (int*)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        scanf("%d", &p[i]);
    }

    int operations = 0;
    while (1) {
        int max = p[0];
        int max_index = 0;
        int low_index = 0;
        for (int i = 0; i < n; i++) {
            if (p[i] > max) {
                max = p[i];
                max_index = i;
            }
            if (p[i] < max && i > low_index) {
                low_index = i;
            }
        }
        if (max_index == n - 1) {
            break;
        }
        int temp = p[max_index];
        for (int i = max_index; i > low_index; i--) {
            p[i] = p[i - 1];
        }
        p[low_index] = temp;
        operations++;
    }
    printf("%d\n", operations);
    free(p);
    return 0;
}
