#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n, p[N], d[N], f[N], g[N];
vector<int> e[N];

void dfs(int u, int fa) {
    d[u] = d[fa] + 1;
    for (int v : e[u]) {
        if (v == fa) continue;
        dfs(v, u);
    }
}

int main() {
    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> p[i];
    }
    for (int i = 1; i < n; i++) {
        int u, v;
        cin >> u >> v;
        e[u].push_back(v);
        e[v].push_back(u);
    }
    dfs(1, 0);
    for (int i = 1; i <= n; i++) {
        f[i] = p[i] + f[i - 1];
        g[i] = max(g[i - 1], f[i]);
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        for (int j : e[i]) {
            if (d[i] > d[j]) {
                ans = max(ans, g[i - 1] + p[i] + p[j] + (n - d[i]) * 2 - 1);
            }
        }
    }
    cout << ans << endl;
    return 0;
}
