#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;

    std::vector<int> p(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> p[i];
    }

    int ans = 0;
    while (true) {
        int max_idx = 0;
        for (int i = 1; i < n; ++i) {
            if (p[i] > p[max_idx]) {
                max_idx = i;
            }
        }

        int high = 0, low = 0;
        for (int i = 0; i <= max_idx; ++i) {
            if (p[i] > p[i + 1]) {
                high++;
            } else {
                low++;
            }
        }

        if (high == n) {
            break;
        }

        std::vector<int> new_p(n);
        for (int i = 0; i < n; ++i) {
            if (i <= max_idx && p[i] > p[i + 1]) {
                new_p[i] = p[i + 1];
            } else {
                new_p[i] = p[i];
            }
        }

        p = new_p;
        ans++;
    }

    std::cout << ans << std::endl;

    return 0;
}
