import java.util.*;
import java.io.*;

public class Main {
    static int N;
    static int[] p;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        N = Integer.parseInt(st.nextToken());
        p = new int[N + 1];
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= N; i++) {
            p[i] = Integer.parseInt(st.nextToken());
        }

        int ans = 0;
        while (true) {
            int max = 0;
            int maxPos = 0;
            int lowPos = 0;
            for (int i = 1; i <= N; i++) {
                if (p[i] > max) {
                    max = p[i];
                    maxPos = i;
                }
                if (p[i] < max && i < maxPos) {
                    lowPos = i;
                }
            }
            if (lowPos == 0) {
                break;
            }
            for (int i = lowPos; i < maxPos; i++) {
                int temp = p[i];
                p[i] = p[i + 1];
                p[i + 1] = temp;
            }
            ans++;
        }
        pw.println(ans);
        pw.close();
    }
}
