#include <stdio.h>
#include <stdlib.h>

#define MAX_N 100
#define MAX_W 1000000000
#define MAX_V 10000000

int knapsack(int n, int w, int w_array[MAX_N], int v_array[MAX_N]) {
    int dp[MAX_N + 1][MAX_W + 1];

    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= w; j++) {
            if (i == 0 || j == 0) {
                dp[i][j] = 0;
            } else if (w_array[i - 1] <= j) {
                dp[i][j] = v_array[i - 1] + dp[i - 1][j - w_array[i - 1]];
            } else {
                dp[i][j] = dp[i - 1][j];
            }
        }
    }

    return dp[n][w];
}

int main() {
    int n, w;
    scanf("%d %d", &n, &w);

    int w_array[MAX_N];
    int v_array[MAX_N];

    for (int i = 0; i < n; i++) {
        scanf("%d %d", &w_array[i], &v_array[i]);
    }

    int max_value = knapsack(n, w, w_array, v_array);

    printf("%d\n", max_value);

    return 0;
}
