#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int knapsack(int n, int w, vector<int>& wv, vector<int>& vv) {
    vector<vector<int>> dp(n + 1, vector<int>(w + 1, 0));
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= w; j++) {
            if (wv[i - 1] > j) {
                dp[i][j] = dp[i - 1][j];
            } else {
                dp[i][j] = max(dp[i - 1][j], dp[i - 1][j - wv[i - 1]] + vv[i - 1]);
            }
        }
    }
    return dp[n][w];
}

int main() {
    int n, w;
    cin >> n >> w;
    vector<int> wv(n), vv(n);
    for (int i = 0; i < n; i++) {
        cin >> wv[i] >> vv[i];
    }
    sort(wv.begin(), wv.end());
    cout << knapsack(n, w, wv, vv) << endl;
    return 0;
}
