#include <stdio.h>
#include <math.h>

#define N 100

typedef struct {
    double x0, y0, x1, y1;
} Line;

typedef struct {
    double x, y;
} Point;

typedef struct {
    double x, y, len;
} Edge;

int n;
Line lines[N];
Point points[N];
Edge edges[N * 2];

double dist(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double cross(Point p1, Point p2, Point p3) {
    return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
}

int next(int i) {
    return (i + 1) % n;
}

int prev(int i) {
    return (i + n - 1) % n;
}

int main() {
    int i, j;
    double x, y, len, min_len = 1e9;

    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%lf %lf %lf %lf", &lines[i].x0, &lines[i].y0, &lines[i].x1, &lines[i].y1);
        points[i].x = lines[i].x0;
        points[i].y = lines[i].y0;
        points[next(i)].x = lines[i].x1;
        points[next(i)].y = lines[i].y1;
    }

    for (i = 0; i < n; i++) {
        edges[i].x = points[i].x;
        edges[i].y = points[i].y;
        edges[i].len = 0;
        edges[i + n].x = points[next(i)].x;
        edges[i + n].y = points[next(i)].y;
        edges[i + n].len = 0;
    }

    for (i = 0; i < n; i++) {
        x = points[i].x;
        y = points[i].y;
        for (j = 0; j < n; j++) {
            if (i == j) continue;
            if (cross(points[i], points[j], points[next(i)]) > 0) {
                len = dist(points[i], points[j]);
                edges[i].len += len;
                edges[j].len += len;
            }
        }
    }

    for (i = 0; i < n; i++) {
        x = points[i].x;
        y = points[i].y;
        for (j = 0; j < n; j++) {
            if (i == j) continue;
            if (cross(points[i], points[j], points[next(i)]) < 0) {
                len = dist(points[i], points[j]);
                edges[i].len -= len;
                edges[j].len -= len;
            }
        }
    }

    for (i = 0; i < n; i++) {
        if (edges[i].len < min_len) min_len = edges[i].len;
    }

    printf("%.3f\n", min_len);

    return 0;
}
