#include <stdio.h>
#include <stdbool.h>

bool is_sorted(int arr[], int n) {
    for (int i = 0; i < n - 1; i++) {
        if (arr[i] > arr[i + 1]) {
            return false;
        }
    }
    return true;
}

bool check_circuit(int n, int d, int wiring[][n]) {
    for (int i = 0; i < d - 1; i++) {
        for (int j = 0; j < n; j++) {
            if (wiring[i][j] < 1 || wiring[i][j] > n) {
                return false;
            }
        }
    }

    int temp[n];
    for (int i = 0; i < d - 1; i++) {
        for (int j = 0; j < n; j++) {
            temp[j] = wiring[i][j];
        }
        if (!is_sorted(temp, n)) {
            return false;
        }
        for (int j = 0; j < n; j++) {
            wiring[i][j] = temp[j];
        }
    }

    return true;
}

int main() {
    int n, d;
    scanf("%d %d", &n, &d);

    int wiring[d - 1][n];
    for (int i = 0; i < d - 1; i++) {
        for (int j = 0; j < n; j++) {
            scanf("%d", &wiring[i][j]);
        }
    }

    if (check_circuit(n, d, wiring)) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }

    return 0;
}
