#include <iostream>
#include <vector>
#include <algorithm>

bool isSorted(const std::vector<int>& arr) {
    for (int i = 0; i < arr.size() - 1; i++) {
        if (arr[i] > arr[i + 1]) {
            return false;
        }
    }
    return true;
}

bool checkCircuit(int n, int d, std::vector<std::vector<int>>& wiring) {
    std::vector<int> arr(n);
    for (int i = 0; i < n; i++) {
        arr[i] = i + 1;
    }

    for (int i = 0; i < d - 1; i++) {
        std::vector<int> new_arr(n);
        for (int j = 0; j < n; j++) {
            new_arr[j] = arr[wiring[i][j] - 1];
        }
        arr = new_arr;
    }

    return isSorted(arr);
}

int main() {
    int n, d;
    std::cin >> n >> d;

    std::vector<std::vector<int>> wiring(d - 1, std::vector<int>(n));
    for (int i = 0; i < d - 1; i++) {
        for (int j = 0; j < n; j++) {
            std::cin >> wiring[i][j];
        }
    }

    if (checkCircuit(n, d, wiring)) {
        std::cout << "Yes" << std::endl;
    } else {
        std::cout << "No" << std::endl;
    }

    return 0;
}
